import React, { useEffect, useState } from 'react';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);
  const storedUser = localStorage.getItem('user');
  const user = JSON.parse(storedUser); 
  console.log(user._id);
  const userId = user._id;

  useEffect(() => {
    // Fetch the user's favorite webtoons from the backend
    const fetchFavorites = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/favourites/${userId}`);
        const data = await response.json();
        console.log("Fetched favorites:", data); // Log the response data to check its structure
        setFavorites(data);
      } catch (error) {
        console.error("Error fetching favorites:", error);
      }
    };

    fetchFavorites();
  }, []);


  return (
    <div>
      <h2>Your Favorite Webtoons</h2>
      {favorites.map((webtoon) => (
        <div key={webtoon.id}>
          <h3>{webtoon.title}</h3>
          <img src={webtoon.image} alt={webtoon.title} />
        </div>
      ))}
    </div>
  );
};

export default Favorites;
