import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
  const [webtoons, setWebtoons] = useState([]);

  useEffect(() => {
    // Fetch webtoons from backend
    fetch('http://localhost:5000//api/webtoons')
      .then((response) => response.json())
      .then((data) => setWebtoons(data))
      .catch((error) => console.error(error));
  }, []);

  return (
    <div className="home-page">
      <h1>Top Webtoons</h1>
      <div className="webtoon-list">
        {webtoons.map((webtoon) => (
          <div key={webtoon._id} className="webtoon-card">
            <Link to={`/webtoon/${webtoon._id}`}>
              <img src={webtoon.thumbnail} alt={webtoon.title} />
              <h3>{webtoon.title}</h3>
              <p>{webtoon.description}</p>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
