import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../App.css';

function WebtoonDetail() {
  const { id } = useParams(); // Extract the webtoon ID from the URL
  const [webtoon, setWebtoon] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch webtoon details from the backend using the ID
    fetch(`http://localhost:5000/api/webtoons/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch webtoon details');
        }
        return response.json();
      })
      .then((data) => setWebtoon(data))
      .catch((err) => setError(err.message));
  }, [id]);

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!webtoon) {
    return <div>Loading...</div>;
  }

  return (
    <div className="webtoon-detail-container">
      <h1>{webtoon.name}</h1>
      <img src={webtoon.img} alt={webtoon.name} className="webtoon-image" />
      <p>{webtoon.description}</p>
      <p><strong>Episodes:</strong> {webtoon.episode}</p>
      <p><strong>Comments:</strong> {webtoon.comment}</p>
      <p><strong>Views:</strong> {webtoon.views}</p>
      {/* Add more details as needed */}
    </div>
  );
}

export default WebtoonDetail;
