import React from 'react';
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css';
import '../App.css';

function HomePage() {
    return (
        <section className="hero">
        <div className="container">
          <div className="hero__slider">
            <div className="hero__items set-bg" style={{ backgroundImage: 'url(/assets/img/hero/hero-1.jpg)' }}>
              <div className="hero__text">
                <label>Fantasy</label> {/* Changed the label */}
                <h2>Fate / Stay Night: Unlimited Blade Works</h2>
                <p>After 30 days of travel across the world...</p>
                <a href="#"><span>Watch Now</span> <i className="fa fa-angle-right"></i></a>
              </div>
            </div>
            <div className="hero__items set-bg" style={{ backgroundImage: 'url(/assets/img/hero/hero-2.jpg)' }}>
              <div className="hero__text">
                <label>Adventure</label> {/* Changed the label */}
                <h2>Another Adventure</h2>
                <p>Explore the mysteries in a new world...</p>
                <a href="#"><span>Watch Now</span> <i className="fa fa-angle-right"></i></a>
              </div>
            </div>
            <div className="hero__items set-bg" style={{ backgroundImage: 'url(/assets/img/hero/hero-3.jpg)' }}>
              <div className="hero__text">
                <label>Action</label> {/* Changed the label */}
                <h2>Action Thriller</h2>
                <p>Join the hero on an action-packed journey...</p>
                <a href="#"><span>Watch Now</span> <i className="fa fa-angle-right"></i></a>
              </div>
            </div>

            {/* Navigation Dots */}
            <div className="nav-dots">
              <span className="dot" onClick={() => {/* Logic to navigate to slide 1 */}}></span>
              <span className="dot" onClick={() => {/* Logic to navigate to slide 2 */}}></span>
              <span className="dot" onClick={() => {/* Logic to navigate to slide 3 */}}></span>
            </div>
          </div>
        </div>
      </section>

    );
}

export default HomePage;
