import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const user = await response.json(); // Get the user data from the response
        console.log('API Response:', user); // Debug: Log the user object to check its structure

        // Ensure the user object contains necessary information, e.g., user._id (userId)
        if (user && user._id) {
          localStorage.setItem('userId', user._id); // Store userId in localStorage
          console.log('Stored User ID:', localStorage.getItem('userId')); // Debug: Check if userId is stored

          if (user.token) {
            localStorage.setItem('token', user.token); // Optionally store token if present
            console.log('Stored Token:', localStorage.getItem('token')); // Debug: Check if token is stored
          }

          // Redirect to /favorites after successful login
          navigate('/');
        } else {
          console.error('User object is invalid or missing _id');
        }
      } else {
        const errorText = await response.text();
        console.error('Login failed:', errorText);
        alert('Login failed. Please check your credentials.');
      }
    } catch (error) {
      console.error('Error during login:', error);
    }
  };


  return (
    <div>
      <section className="normal-breadcrumb">
        <div className="container-fluid">
          <div className="row">
            <div className="col-lg-12 text-center position-relative">
              <img src="assets/img/normal-breadcrumb.jpg" alt="Breadcrumb Background" className="img-fluid" />
              <div className="normal__breadcrumb__text">
                <h2>Login</h2>
                <p>Welcome to the official Anime blog.</p>
              </div>
            </div>
          </div>
        </div>
      </section>

      <section className="login spad">
        <div className="container">
          <div className="row">
            <div className="col-lg-6">
              <div className="login__form">
                <h3>Login</h3>
                <form onSubmit={handleSubmit}>
                  <div className="input__item">
                    <input
                      type="email"
                      value={email}
                      onChange={(e) => setEmail(e.target.value)}
                      placeholder="Email address"
                      required
                    />
                    <span className="icon_mail"></span>
                  </div>
                  <div className="input__item">
                    <input
                      type="password"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      placeholder="Password"
                      required
                    />
                    <span className="icon_lock"></span>
                  </div>
                  <button type="submit" className="site-btn">Login Now</button>
                </form>
                <a href="#" className="forget_pass">Forgot Your Password?</a>
              </div>
            </div>
            <div className="col-lg-6 d-flex justify-content-center align-items-center">
              <div className="login__register text-center">
                <h3>Don't Have An Account?</h3>
                <a href="/register" className="primary-btn">Register Now</a>
              </div>
            </div>
          </div>

          <div className="login__social">
            <div className="row d-flex justify-content-center">
              <div className="col-lg-6">
                <div className="login__social__links">
                  <span>or</span>
                  <ul>
                    <li><a href="#" className="facebook"><i className="fa fa-facebook"></i> Sign in With Facebook</a></li>
                    <li><a href="#" className="google"><i className="fa fa-google"></i> Sign in With Google</a></li>
                    <li><a href="#" className="twitter"><i className="fa fa-twitter"></i> Sign in With Twitter</a></li>
                  </ul>
                </div>
              </div>
            </div>
          </div>

        </div>
      </section>
    </div>
  );
};

export default Login;
