import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Card,
  CardMedia,
  CardContent,
  Typography,
  CircularProgress,
  Alert,
} from '@mui/material';
import '../App.css';

function WebtoonDetail() {
  const { id } = useParams(); // Extract the webtoon ID from the URL
  const [webtoon, setWebtoon] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch webtoon details from the backend using the ID
    fetch(`http://localhost:5000/api/webtoons/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch webtoon details');
        }
        return response.json();
      })
      .then((data) => setWebtoon(data))
      .catch((err) => setError(err.message));
  }, [id]);

  if (error) {
    return (
      <Container>
        <Alert severity="error">Error: {error}</Alert>
      </Container>
    );
  }

  if (!webtoon) {
    return (
      <Container>
        <CircularProgress />
      </Container>
    );
  }

  return (
    <Container maxWidth="md" className="webtoon-detail-container">
      <Card elevation={3}>
        <CardMedia
          component="img"
          height="300"
          image={webtoon.img}
          alt={webtoon.name}
        />
        <CardContent>
          <Typography variant="h4" component="h1" gutterBottom>
            {webtoon.name}
          </Typography>
          <Typography variant="body1" paragraph>
            {webtoon.description}
          </Typography>
          <Typography variant="h6">Details:</Typography>
          <Typography variant="body2">
            <strong>Episodes:</strong> {webtoon.episode}
          </Typography>
          <Typography variant="body2">
            <strong>Comments:</strong> {webtoon.comment}
          </Typography>
          <Typography variant="body2">
            <strong>Views:</strong> {webtoon.views}
          </Typography>
        </CardContent>
      </Card>
    </Container>
  );
}

export default WebtoonDetail;
