import React, { useEffect, useState } from 'react';

const Favorites = () => {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    // Fetch the user's favorite webtoons from the backend
    const fetchFavorites = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/favourites/details`);
        const data = await response.json();
        console.log("Fetched favorites:", data); // Log the response data to check its structure
        if (Array.isArray(data)) {
          setFavorites(data);  // Set only if the response is an array
        } else {
          console.error("Unexpected data format:", data);
        }
      } catch (error) {
        console.error("Error fetching favorites:", error);
      }
    };

    fetchFavorites();
  }, []);

  return (
    <div>
      <h2>Your Favorite Webtoons</h2>
