import React, { useState, useEffect } from 'react';

function WebtoonDetailPage({ match }) {
  const [webtoon, setWebtoon] = useState(null);

  useEffect(() => {
    // Fetch the specific webtoon details from backend
    fetch(`/api/webtoons/${match.params.id}`)
      .then((response) => response.json())
      .then((data) => setWebtoon(data))
      .catch((error) => console.error(error));
  }, [match.params.id]);

  if (!webtoon) return <div>Loading...</div>;

  return (
    <div className="webtoon-detail-page">
      <img src={webtoon.image} alt={webtoon.title} />
      <h1>{webtoon.title}</h1>
      <p>{webtoon.description}</p>

      <div className="comments-section">
        <h3>Comments</h3>
        {/* Add user comments input and display */}
      </div>
    </div>
  );
}

export default WebtoonDetailPage;
