import React, { useEffect, useState } from 'react';

const Navbar = () => {
  const [username, setUsername] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    console.log(storedUser);
    if (storedUser && storedUser !== 'undefined' && storedUser !== 'null') {
      try {
        const user = JSON.parse(storedUser);
        if (user && user.username) {
          setUsername(user.username);
        }
      } catch (error) {
        console.error("Error parsing user data:", error);
      }
    }
  }, []);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <header className="header sticky">
      <div className="container">
        <div className="row">
          <div className="col-lg-2">
            <div className="header__logo">
              <a href="/">
                <img src="/assets/img/logo.png" alt="Logo" />
              </a>
            </div>
          </div>
          <div className="col-lg-8">
            <div className={`header__nav ${isMobileMenuOpen ? 'open' : ''}`}>
              <nav className="header__menu mobile-menu">
                <ul>
                  <li><a href="/">Home</a></li>
                  <li><a href="/favorites">Favourite</a></li>
                  <li><a href="#">Contacts</a></li>
                  {username ? (
                    <li><a href="#">Hello, {username}</a></li>
                  ) : (
                    <li><a href="/login">Login</a></li>
                  )}
                </ul>
              </nav>
            </div>
          </div>
          <div className="col-lg-2">
            <div className="header__right">
              <a href="#" className="search-switch"><i className="fas fa-search"></i></a>
              <a href="/login"><i className="fas fa-user"></i></a>
              {/* Mobile menu toggle button */}
              <button className="mobile-menu-btn" onClick={toggleMobileMenu}>
                <i className={isMobileMenuOpen ? 'fas fa-times' : 'fas fa-bars'}></i>
              </button>
            </div>
          </div>
        </div>
        <div id="mobile-menu-wrap"></div>
      </div>
    </header>
  );
};

export default Navbar;
