import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
  const [username, setUsername] = useState(null);
  const navigate = useNavigate(); // Use useNavigate for redirection after logout

  useEffect(() => {
    // Retrieve the user information from localStorage
    const storedUser = localStorage.getItem('user');
    console.log(storedUser);

    // Only attempt to parse if the value exists and is valid JSON
    if (storedUser && storedUser !== 'undefined' && storedUser !== 'null') {
      try {
        const user = JSON.parse(storedUser); // Parse the JSON string
        if (user && user.username) {
          setUsername(user.username); // Set the username if it exists
        }
      } catch (error) {
        console.error("Error parsing user data:", error); // Log parsing error
      }
    }
  }, []);

  const handleLogout = () => {
    // Clear user data from localStorage
    localStorage.removeItem('user');
    setUsername(null); // Clear the username from state
    navigate('/login'); // Redirect to login page
  };

  return (
    <header className="header">
      <div className="container">
        <div className="row">
          <div className="col-lg-2">
            <div className="header__logo">
              <a href="/">
                <img src="/assets/img/logo.png" alt="" />
              </a>
            </div>
          </div>
          <div className="col-lg-8">
            <div className="header__nav">
              <nav className="header__menu mobile-menu">
                <ul>
                  <li><a href="/">Home</a></li>
                  <li><a href="/favourite">Favourite</a></li>
                  <li><a href="#">Contacts</a></li>
                  {username ? (
                    <>
                      <li><a href="#">Hello, {username}</a></li>
                      <li><a href="#" onClick={handleLogout}>Logout</a></li>
                    </>
                  ) : (
                    <li><a href="/login">Login</a></li>
                  )}
                </ul>
              </nav>
            </div>
          </div>
          <div className="col-lg-2">
            <div className="header__right">
              <a href="#" className="search-switch"><i className="fas fa-search"></i></a>
              <a href="/login"><i className="fas fa-user"></i></a>
            </div>
          </div>
        </div>
        <div id="mobile-menu-wrap"></div>
      </div>
    </header>
  );
};

export default Navbar;
