import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './HomePage.css'; // Add this for custom CSS

function HomePage() {
  const [webtoons, setWebtoons] = useState([
    // Hardcoded for now, replace with your fetch call
    { _id: 1, title: 'Adventure Webtoon', description: 'An amazing journey awaits!', thumbnail: '/assets/img/webtoon1.jpg' },
    { _id: 2, title: 'Action Webtoon', description: 'Epic battles in every chapter!', thumbnail: '/assets/img/webtoon2.jpg' },
  ]);

  const [currentSlide, setCurrentSlide] = useState(0);
  const heroItems = [
    {
      id: 1,
      title: 'Fate / Stay Night: Unlimited Blade Works',
      description: 'After 30 days of travel across the world...',
      image: '/assets/img/hero/hero-1.jpg',
    },
    {
      id: 2,
      title: 'Another Adventure',
      description: 'An epic adventure awaits...',
      image: '/assets/img/hero/hero-2.jpg',
    },
    {
      id: 3,
      title: 'Third Hero Story',
      description: 'The journey continues...',
      image: '/assets/img/hero/hero-3.jpg',
    },
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % heroItems.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + heroItems.length) % heroItems.length);
  };

  return (
    <div>
      <section className="hero">
        <div className="carousel-container">
          <div className="carousel-slide" style={{ backgroundImage: `url(${heroItems[currentSlide].image})` }}>
            <div className="carousel-content">
              <div className="label">Adventure</div>
              <h2>{heroItems[currentSlide].title}</h2>
              <p>{heroItems[currentSlide].description}</p>
              <a href="#"><span>Watch Now</span> <i className="fa fa-angle-right"></i></a>
            </div>
          </div>
          <button className="carousel-arrow left-arrow" onClick={prevSlide}>&lt;</button>
          <button className="carousel-arrow right-arrow" onClick={nextSlide}>&gt;</button>
        </div>
      </section>

      <div className="home-page">
        <h1>Top Webtoons</h1>
        <div className="webtoon-list">
          {webtoons.map((webtoon) => (
            <div key={webtoon._id} className="webtoon-card">
              <Link to={`/webtoon/${webtoon._id}`}>
                <img src={webtoon.thumbnail} alt={webtoon.title} />
                <h3>{webtoon.title}</h3>
                <p>{webtoon.description}</p>
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default HomePage;
