import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function HomePage() {
  const [webtoons, setWebtoons] = useState([]);

  useEffect(() => {
    // Fetch webtoons from backend
    fetch('http://localhost:5000/api/webtoons')
      .then((response) => response.json())
      .then((data) => setWebtoons(data))
      .catch((error) => console.error(error));
  }, []);

  useEffect(() => {
    // Initialize the carousel here if needed
    const owlCarousel = window.$('.owl-carousel');
    if (owlCarousel.length) {
      owlCarousel.owlCarousel({
        loop: true,
        margin: 10,
        nav: true,
        items: 1, // Number of items to show
      });
    }

    // Set background images based on data-setbg attribute
    const setBgElements = document.querySelectorAll('.set-bg');
    setBgElements.forEach((element) => {
      const bg = element.getAttribute('data-setbg');
      element.style.backgroundImage = `url(${bg})`;
    });
  }, []);

  return (
    <div>
      <section className="hero">
        <div className="container">
          <div className="hero__slider owl-carousel">
            <div className="hero__items set-bg" data-setbg="/assets/img/hero/hero-1.jpg">
              <div className="row">
                <div className="col-lg-6">
                  <div className="hero__text">
                    <div className="label">Adventure</div>
                    <h2>Fate / Stay Night: Unlimited Blade Works</h2>
                    <p>After 30 days of travel across the world...</p>
                    <a href="#"><span>Watch Now</span> <i className="fa fa-angle-right"></i></a>
                  </div>
                </div>
              </div>
            </div>
            <div className="hero__items set-bg" data-setbg="/assets/img/hero/hero-2.jpg">
              <div className="row">
                <div className="col-lg-6">
                  <div className="hero__text">
                    <div className="label">Adventure</div>
                    <h2>Another Title</h2>
                    <p>After 30 days of travel across the world...</p>
                    <a href="#"><span>Watch Now</span> <i className="fa fa-angle-right"></i></a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      <div className="home-page">
        <h1>Top Webtoons</h1>
        <div className="webtoon-list">
          {webtoons.map((webtoon) => (
            <div key={webtoon._id} className="webtoon-card">
              <Link to={`/webtoon/${webtoon._id}`}>
                <img src={webtoon.thumbnail} alt={webtoon.title} />
                <h3>{webtoon.title}</h3>
                <p>{webtoon.description}</p>
              </Link>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default HomePage;
